import React from 'react';
import Card from 'react-bootstrap/Card';
import CardDeck from 'react-bootstrap/CardDeck';
import Image from 'react-bootstrap/Image';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

// skills
import { skills } from './skills-data';

import './skills.styles.css';

const Skills = () => {
	return (
		<div className="pt-3 pb-3" id="events">
			<h1 className="text-center font-details-b pb-4">OTHER EVENTS</h1>
			<CardDeck>
				<Row className="d-flex justify-content-around">
					{/* Frontend */}
					<Col md={12}>
						<Card className="focus mt-2 mb-2">
							<Card.Body>
								{/* Frontend */}
								<Card.Title className="text-center  card-title">HALDI</Card.Title>
								<hr />
								<Card.Text className="card-text d-flex justify-content-start flex-column">
									{skills.frontend.map((skill, index) => (
										<><span className="p-2" key={index}>
										<a
											className="text-dark text-decoration-none"
											href={skill.link}
											target="_blank"
											rel="noopener noreferrer"
										>
											<Image
												src={skill.imgSrc}
												alt={skill.imgAltText}
												rounded
												className='haldi-image'

											></Image>{' '}
											</a>
										</span>
										<strong>HALDI Kissed !!</strong>
										<span>Haldi  is a pre-wedding ceremony performed a day or two before the wedding. Family members and friends playfully slather the bride and groom in a paste of turmeric, oil, and milk to prevent evil spirits from harming the to-be-wed couple.</span>
										<span>
											<strong>Event Date : </strong>
											February 17th, 2024 - Morning 7 AM IST
										</span>
										</>


									))}
								</Card.Text>
							</Card.Body>
						</Card>
					</Col>

					{/* Programming Languages */}
					<Col md={4}>
						<Card className="focus mt-2 mb-2">
							<Card.Body>
								<Card.Title className="text-center  card-title">Programming Languages</Card.Title>
								<hr />
								<Card.Text className="card-text d-flex justify-content-start flex-column">
									{skills.programmingLanguages.map((skill, index) => (
										<span className="p-2" key={index}>
											<a
												className="text-dark text-decoration-none"
												href={skill.link}
												target="_blank"
												rel="noopener noreferrer"
											>
												<Image
													src={skill.imgSrc}
													alt={skill.imgAltText}
													rounded
													className="image-style m-1"
												></Image>{' '}
												{skill.skillName}
											</a>
										</span>
									))}
								</Card.Text>
							</Card.Body>
						</Card>

						{/* Database */}

						<Card className="focus mt-2 mb-2">
							<Card.Body>
								<Card.Title className="text-center  card-title">Database</Card.Title>
								<hr />
								<Card.Text className="card-text d-flex justify-content-start flex-column">
									{skills.databases.map((skill, index) => (
										<span className="p-2" key={index}>
											<a
												className="text-dark text-decoration-none"
												href={skill.link}
												target="_blank"
												rel="noopener noreferrer"
											>
												<Image
													src={skill.imgSrc}
													alt={skill.imgAltText}
													rounded
													className="image-style m-1"
												></Image>{' '}
												{skill.skillName}
											</a>
										</span>
									))}
								</Card.Text>
							</Card.Body>
						</Card>
						{/* Version Control */}

						<Card className="focus mt-2 mb-2">
							<Card.Body>
								<Card.Title className="text-center  card-title">Version Control</Card.Title>
								<hr />
								<Card.Text className="card-text d-flex justify-content-start flex-column">
									<span className="p-2">
										<a
											className="text-dark text-decoration-none"
											href={skills.versionControl[0].link}
											target="_blank"
											rel="noopener noreferrer"
										>
											<Image
												src={skills.versionControl[0].imgSrc}
												alt={skills.versionControl[0].imgAltText}
												rounded
												className="image-style m-1"
											></Image>{' '}
											{skills.versionControl[0].skillName}
										</a>
									</span>
									<span>
										<a
											className="text-dark text-decoration-none"
											href={skills.versionControl[1].link}
											target="_blank"
											rel="noopener noreferrer"
										>
											<Image
												src={skills.versionControl[1].imgSrc}
												alt={skills.versionControl[1].imgAltText}
												rounded
												className="image-style m-1"
											></Image>{' '}
											{skills.versionControl[1].skillName}
										</a>
									</span>
								</Card.Text>
							</Card.Body>
						</Card>

						{/* Monitoring Control */}

						<Card className="focus mt-2 mb-2">
							<Card.Body>
								<Card.Title className="text-center  card-title">Monitor control</Card.Title>
								<hr />
								<Card.Text className="card-text d-flex justify-content-start flex-column">
									{skills.monitoring.map((skill, index) => (
										<span className="p-2" key={index}>
											<a
												className="text-dark text-decoration-none"
												href={skill.link}
												target="_blank"
												rel="noopener noreferrer"
											>
												<Image
													src={skill.imgSrc}
													alt={skill.imgAltText}
													rounded
													className="image-style m-1"
												></Image>{' '}
												{skill.skillName}
											</a>
										</span>
									))}
								</Card.Text>
							</Card.Body>
						</Card>
					</Col>
				</Row>
			</CardDeck>
		</div>
	);
};

export default Skills;
